import React, { useState, useEffect } from "react";
import styles from "../../../styles/Register.module.css";
import {
  CheckEmail,
  CheckPasswordLength,
  CheckIfPasswordsAreMatching,
  CheckIfUserAdult,
  CheckIfIsntEmpty,
  CheckIfFormIsValid,
} from "../../../utils/validation";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    surname: "",
    birthdayDate: "",
    email: "",
    password: "",
    repeatedPassword: "",
  });

  const [formErrors, setFormErrors] = useState({
    nameErr: false,
    surnameErr: false,
    birthdayDateErr: false,
    emailErr: false,
    passwordErr: false,
    repeatedPasswordErr: false,
    isFormValid: false,
  });

  const setErrValue = (fieldName, value) => {
    setFormErrors((prevState) => ({
      ...prevState,
      [fieldName]: value,
    }));
  };

  const ValidationCheck = () => {
    setErrValue("nameErr", !CheckIfIsntEmpty(formData.name) ? true : false);
    setErrValue(
      "surnameErr",
      !CheckIfIsntEmpty(formData.surname) ? true : false
    );
    setErrValue(
      "birthdayErr",
      !CheckIfUserAdult(formData.birthdayDate) ? true : false
    );
    setErrValue("emailErr", !CheckEmail(formData.email) ? true : false);
    setErrValue(
      "passwordErr",
      !CheckPasswordLength(formData.password) ? true : false
    );
    setErrValue(
      "repeatedPassword",
      !CheckIfPasswordsAreMatching(formData.password, formData.repeatedPassword)
        ? true
        : false
    );

    if (
      !formErrors.nameErr &&
      !formErrors.surnameErr &&
      !formErrors.emailErr &&
      !formErrors.birthdayDateErr &&
      !formErrors.passwordErr &&
      !formErrors.repeatedPasswordErr
    ) {
      setErrValue("isFormValid", true);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  async function registerUser(event) {
    ValidationCheck();
    if (
      formErrors.isFormValid
    ) {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log(data);
    } else {
      console.log("form isnt valid");
    }
  }

  return (
    <div className={styles.container}>
      <div className={styles["form-container"]}>
        <form className={styles["form-section"]} onSubmit={registerUser}>
          <h1>REGISTER</h1>
          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>NAME</label>
            <input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              className={styles["register-input"]}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>SURNAME</label>
            <input
              name="surname"
              value={formData.surname}
              onChange={handleInputChange}
              className={styles["register-input"]}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>DATE OF BIRTH</label>
            <input
              name="dateOfBirth"
              value={formData.birthdayDate}
              onChange={handleInputChange}
              className={styles["register-input"]}
              type="date"
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]} type="email">
              E-MAIL
            </label>
            <input
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              className={styles["register-input"]}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>PASSWORD</label>
            <input
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              className={styles["register-input"]}
              type="password"
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}> REPEAT PASSWORD</label>
            <input
              name="repeatedPassword"
              value={formData.repeatedPassword}
              onChange={handleInputChange}
              className={styles["register-input"]}
              type="password"
            ></input>
          </div>
          <div className={styles["register-buttons"]}>
            <button className={styles["back-btn"]}>&#8592;</button>
            <button className={styles["register-btn"]} type="submit">
              REGISTER
            </button>
          </div>
        </form>
        <div className={styles["photo-section"]}></div>
      </div>
    </div>
  );
};
export default Register;
