import React, { useState, useEffect } from "react";
import styles from "../../../styles/Register.module.css";
import {
  CheckEmail,
  CheckPasswordLength,
  CheckIfPasswordsAreMatching,
  CheckIfUserAdult,
  CheckIfIsntEmpty,
} from "../../../utils/validation";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    surname: "",
    birthdayDate: "",
    email: "",
    password: "",
    repeatedPassword: "",
  });

  const [formErrors, setFormErrors] = useState({
    nameErr: false,
    surnameErr: false,
    birthdayDateErr: false,
    emailErr: false,
    passwordErr: false,
    repeatedPasswordErr: false,
  });

  const [isFormValid, setIsFormValid] = useState(false);

  async function setErrValue(fieldName, value) {
    setFormErrors((prevState) => ({
      ...prevState,
      [fieldName]: value,
    }));
  }

  function ValidationCheck() {
    console.log("sprawdzam warunki");
    setErrValue("nameErr", !CheckIfIsntEmpty(formData.name) ? true : false);
    setErrValue(
      "surnameErr",
      !CheckIfIsntEmpty(formData.surname) ? true : false
    );
    setErrValue(
      "birthdayDateErr",
      !CheckIfUserAdult(formData.birthdayDate) ? true : false
    );
    setErrValue("emailErr", !CheckEmail(formData.email) ? true : false);
    setErrValue(
      "passwordErr",
      !CheckPasswordLength(formData.password) ? true : false
    );
    setErrValue(
      "repeatedPasswordErr",
      !CheckIfPasswordsAreMatching(formData.password, formData.repeatedPassword)
        ? true
        : false
    );
    console.log("koniec sprawdzania warunkow");
    console.log("stan name error : ", formErrors.nameErr);
    formCheck();
  }

  function formCheck() {
    console.log("sprawdzam czy form jest zgodny");
    if (formErrors.nameErr === true) {
      console.log("ustawiam form na nieprawidlowy");
      setIsFormValid(false);
    } else if (formErrors.nameErr == false) {
      console.log("ustawiam form na prawidlowy");
      console.log("stan : ");
      console.log(formErrors.nameErr);
      setIsFormValid(true);
    }
  }

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  async function registerUser(event) {
    event.preventDefault();
    await ValidationCheck();
    if (isFormValid) {
      console.log("everything is ok!!!!!!!!");
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log(data);
    } else {
      await console.log("form isnt valid");
      return;
    }
  }

  return (
    <div className={styles.container}>
      <div className={styles["form-container"]}>
        <form className={styles["form-section"]} onSubmit={registerUser}>
          <h1>REGISTER</h1>
          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>NAME</label>
            <input
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.nameErr ? styles.inputBlur : ""
              }`}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>SURNAME</label>
            <input
              name="surname"
              value={formData.surname}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.surnameErr ? styles.inputBlur : ""
              }`}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>DATE OF BIRTH</label>
            <input
              name="birthdayDate"
              value={formData.birthdayDate}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.birthdayDateErr ? styles.inputBlur : ""
              }`}
              type="date"
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]} type="email">
              E-MAIL
            </label>
            <input
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.emailErr ? styles.inputBlur : ""
              }`}
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}>PASSWORD</label>
            <input
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.passwordErr ? styles.inputBlur : ""
              }`}
              type="password"
            ></input>
          </div>

          <div className={styles["register-input--section"]}>
            <label className={styles["register-label"]}> REPEAT PASSWORD</label>
            <input
              name="repeatedPassword"
              value={formData.repeatedPassword}
              onChange={handleInputChange}
              className={`${styles["register-input"]}  ${
                formErrors.repeatedPasswordErr ? styles.inputBlur : ""
              }`}
              type="password"
            ></input>
          </div>
          <div className={styles["register-buttons"]}>
            <button className={styles["back-btn"]}>&#8592;</button>
            <button className={styles["register-btn"]} type="submit">
              REGISTER
            </button>
          </div>
        </form>
        <div className={styles["photo-section"]}></div>
      </div>
    </div>
  );
};
export default Register;
