/**
 * 验证手机号
 * @param {*} phone 
 */
function checkPhone(phone){
    const reg = /^0?1[3-9][0-9]\d{8}$/;
        if (!reg.test(phone)) {
            return false;
        }
        return true;
};
/**
 * 验证身份证号
 * @param {*} card 
 */
function checkIDCard(card){
    var reg = /(^\d{15}$)|(^\d{18}$)|(^\d{17}(\d|X|x)$)/;  
    if(reg.test(card))  
    {  
      return true;
    }
    return false;  
};

module.exports.checkPhone = checkPhone;
module.exports.checkIDCard = checkIDCard;